import React, {useState} from "react";

const Formulario = () => {
  const initialReserva = {
    nombres: "",
    email: "",
    telefono: "",
    servicio: "",
    num_personas: "",
    fecha: "",
    terminos: "",
  };
  const [form, setForm] = useState(initialReserva);
  const handleChange = (e) => {
    setForm({
      ...form,
      [e.target.name]: e.target.value,
    });
  };

  const handleChecked = (e) => {
    setForm({
      ...form,
      [e.target.name]: e.target.checked,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert("El formulario se ha enviado");
  };
  return (
    <>
      <div onSubmit={handleSubmit} className="col-sm-6" style={{marginBottom:"2rem"}}>
        <div className="form-group  ">
          <select
            className="form-control"
            name="servicio"
            id="servicio"
            placeholder="Seleccionar el servicio"
            onChange={handleChange}
          >
            <option>Seleccionar el servicio</option>
            <option>Celebración de Cumpleaños</option>
            <option>Aniversarios</option>
            <option>Fiestas Infantiles</option>
            <option>Declaraciones o propuestas</option>
            <option>Despedidas</option>
            <option>Cena con amigos</option>
          </select>
        </div>
        <div className="form-group">
          <input
            type="text"
            name="nombres"
            className="form-control "
            id="nombre"
            placeholder="Nombre completo"
            onChange={handleChange}
          />
        </div>
        <div className="form-group">
          <input
            type="text"
            name="telefono"
            className="form-control "
            id="telefono"
            placeholder="Telefono y/o celular"
            onChange={handleChange}
          />
        </div>
        <div className="form-group">
          <input
            type="email"
            name="email"
            className=" form-control"
            id="correo"
            placeholder="Correo electrónico"
            onChange={handleChange}
          />
        </div>
        <div className="form-group">
          <input type="datetime-local" id="fecha_rese" name="fecha" onChange={useState} />
        </div>
        
        <div className="form-group">
          <input
            min={1}
            type="number"
            name="num_personas"
            className="form-control "
            id="npersonas"
            placeholder="Numero personas"
            onChange={useState}
          />
        </div>
        <div>
          <textarea
            className="form-control "
            rows={5}
            id="mensaje"
            placeholder="Indicaciones especiales"
            defaultValue={"Sin palabras"}
          />
        </div>
        <div className="form-check">
          <label className="form-check-label">
            <input type="checkbox" name="terminos" className="form-check-input" onChange={useState}/>
            Acepto terminos y condiciones
          </label>
        </div>
        <div className="btn_enviar">
          <button
            type="button"
            className="btn naranja w-50 text-white"
            onclick="sendMail()"
          >
            ENVIAR
          </button>
        </div>
      </div>
    </>
  );
};

export default Formulario;
